import React, { Component } from 'react';
import './App.css';
import Table from './Components/Table';
import Input from './Components/Input';
import MyCard from './Components/Card';
import axios from "axios";

import socketIOClient from "socket.io-client";

class App extends Component {
  state = {
    name:"",

    mail:"",
    id:"",
    foundWorkes: [],
    selectStudent:[],
    endpoint: "localhost:4002",
    color: 'orange',
    studentForInsert: {},

  };



  ButtonInsert = () => {

   let name=this.state.name;
    let age=this.state.age;
    let mail=this.state.mail;


    const socket = socketIOClient(this.state.endpoint);
    socket.emit('insert student', name,age,mail) // change 'red' to this.state.color 

  }

componentDidMount=()=>{
  this.getAllStudents();

  const socket = socketIOClient(this.state.endpoint);

      socket.on('insert student', (studentForInsert) => {

          let AllStudOld=this.state.foundStudents;
          AllStudOld.push(studentForInsert)

          this.setState({ foundStudents: AllStudOld });

          console.log(studentForInsert);

      })

}

getAllWorkes=()=>{
  var self=this;
  axios.get(`http://localhost:4002/getWorkes`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ foundWorkes: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

printCards=()=>{
  let printArray  = [];
  for(let item of this.state.foundWorkes)
  {
      console.log(item);
      printArray.push(<MyCard item={item}/>)

  }

  return printArray;
}

  render() {
    return (
      <div className="container">
      <div className="row">

      <div className="col-8">
      <h3>All workes: </h3>
       <MyCard date></MyCard>
      </div>


      <div className="col-4">
      <h1>Add workes:</h1>
        <Input></Input> 
      </div>

      </div>
      </div>

    );
  }
}

export default App;
