import React, { Component }  from 'react';
import './Card.css';
import ToggleButton from 'react-bootstrap/ToggleButton'
import {Card,Button} from 'react-bootstrap';

class MyCard extends Component {
    // componentDidMount=()=>{
    //     let checkOn= this.prop.on_off === '1'?(type='checkbox' checked) type='checkbox' 
    //     }

checkToggle() {
    if (this.props.on_off == "1") {
        return <input type="checkbox" checked id={this.props.id} className='togBtn'></input>
    } else {
        return <input type="checkbox" id={this.props.id} className='togBtn'></input>
    }
}

render() {

    console.log("props : " , this.props)
    return (

        <div className='card' style={{width:18+'rem'}}>
        {/* <img className='card-img-top' src="+arr_prin.image_url+" alt='Card image cap'>; */}
        <div className='card-body'>
        <h5 className='card-title'>{this.props.name}</h5>
        <p className='card-text'>{this.props.mail}</p>


        <label className='switch'>
        {this.checkToggle}
        <span className='slider round'></span>
		</label>

         </div>
        </div> 
    ); 
}
}

export default MyCard
