import React, { Component } from 'react';
import { Button, Form} from 'react-bootstrap';

// import axios from "axios";
import socketIOClient from "socket.io-client";

class table extends Component {
    state = {

        name:"",
        age:"",
        mail:"",
        endpoint: "localhost:4002",
      };

    render() {
    console.log("props : " , this.props)

// handleChange=(event,type)=> {
//     if (type === 0){
//       this.setState({name: event.target.value});
//       console.log("change",this.state.name)
//     }
//     if (type === 1) {
//       this.setState({age: event.target.value});
//       console.log("change",this.state.age)
//     }
//     if (type === 2) {
//       this.setState({mail: event.target.value});
//       console.log("change",this.state.mail)
//     }
//   }

// sending sockets
ButtonInsert = () => {
    // let studentForInsert={};
    let name=this.state.name;
    let age=this.state.age;
    let mail=this.state.mail;
    // this.setState({ studentForInsert: studentForInsert });

    const socket = socketIOClient(this.state.endpoint);
    socket.emit('insert student', name,age,mail) // change 'red' to this.state.color 

  }
    return (
        <div>
          <Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email name</Form.Label>
    <Form.Control type="name" placeholder="Enter name" onChange = {(event,newValue) => this.setState({name:newValue})}/>
    <Form.Text className="text-muted">
      {/* We'll never share your email with anyone else. */}
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Age</Form.Label>
    <Form.Control type="age" placeholder="Age" onChange = {(event,newValue) => this.setState({age:newValue})}/>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Mail</Form.Label>
    <Form.Control type="mail" placeholder="Mail" onChange = {(event,newValue) => this.setState({mail:newValue})}/>
  </Form.Group>

  {/* <Form.Group controlId="formBasicChecbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group> */}
  <Button variant="primary" type="submit" onClick={() =>this.ButtonInsert()}>
    Submit
  </Button>
</Form>
        </div>
      );
}
}

export default table
