import React, { Component } from 'react';
import './App.css';
import Input from './Components/Input';
import MyCard from './Components/Card';
import axios from "axios";

class App extends Component {
  state = {
    name:"",

    mail:"",
    id:"",
    foundWorkes: [],
    selectStudent:[],
    endpoint: "localhost:4002",
    color: 'orange',
    studentForInsert: {},

  };


// componentDidMount=()=>{
//   this.getAllWorkes();
// }

getAllWorkes=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getWorkes`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ foundWorkes: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

printCards=()=>{
  let printArray  = [];
  for(let item of this.state.foundWorkes)
  {
      console.log(item);
      printArray.push(<MyCard checkOn={item.on_off} name={item.name} mail={item.mail} id={item.id}/>)

  }
  return printArray;
}

  render() {
    return (
      <div className="container">
      <div className="row">

      <div className="col-8">
      <h3>All workes: </h3>
           {this.printCards()}
      </div>


      <div className="col-4">
      <h1>Add workes:</h1>
        <Input></Input> 
      </div>

      </div>
      </div>

    );
  }
}

export default App;
