import React, { Component }  from 'react';
import Modal from 'react-awesome-modal';
import {Form} from 'react-bootstrap';

import './ButtonUpd.css';

import axios from "axios";

class ButtonUpd extends Component {

  constructor(props) {
    super(props);
    this.state = {
        visible : false,

        image:"",
        nameVac:"",
        location:"",
        dateFrom:"",
        dateTo:"",
        price:""
    }
}

openModal() {
    this.setState({
        visible : true
    });
}

closeModal() {
    this.setState({
        visible : false
    });
}


handleClick = (idVac) => {
   // event.preventDefault();
    let image=this.state.image;
    let nameVac=this.state.nameVac; 
    let location=this.state.location;
    let dateFrom=this.state.dateFrom; 
    let dateTo=this.state.dateTo;
    let price=this.state.price;

    //console.log("props : " , this.props)
    console.log('idVac',idVac) 
    console.log('nameVac',nameVac) 
    this.closeModal();

    // axios.get(`http://localhost:5000/updateVacation?idVac=${idVac}`)
    // .then(function(response){

    //   console.log(response.data);

    // })
    // .catch(function(error){
    //    console.log(error);
    // });

}


render() {

    console.log("props : " , this.props)

    return (
     <div>

       <i className="far fa-edit" onClick={() => this.openModal()}></i>

       <section>

                <Modal 
                    visible={this.state.visible}
                    width="420"
                    height="700"
                    effect="fadeInUp"
                    onClickAway={() => this.closeModal()}
                >
                    <div className="container">
                    <div className="row modalHeder">  
                    <div className="col-10">
                      <h3>Edit vacation</h3>
                    </div>  
                    <div className="col-2"> 
                      <i className="fas fa-times" onClick={() => this.closeModal()}></i>
                    </div>
                    </div>
                      <hr></hr>
                      <Form>
  <Form.Group controlId="nameVac">
    <Form.Label>Name</Form.Label>
    <Form.Control type="nameVac" value={this.props.title} onChange = {(event,target) => this.setState({nameVac:event.target.value})}/>
    <Form.Text className="text-muted">
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicMail">
    <Form.Label>Comment</Form.Label>
    <Form.Control type="comment" placeholder="Comment" onChange = {(event,target) => this.setState({comment:event.target.value})}/>
  </Form.Group>

</Form>
                      <hr></hr>
                      <div className="row">
  <div className="col-6">
  <a className="button btn but" onClick={() =>this.handleClick(this.props.idVac)} href="/HomeAdmin">Delete</a>
  </div>
  <div className="col-6">
  <a className="button btn but" onClick={() => this.closeModal()} href="/HomeAdmin">Cancel</a>
  </div>
</div>

                    </div>
                </Modal>
            </section>

    </div>


            ); 
}
}

export default ButtonUpd
