import React, { Component } from 'react';
import './App.css';
//import Left from './Components/LeftPage'
//import InputT from './Components/InputText'
import Form from './Components/Form'
import Table from './Components/Table'

class App extends Component {

    Counter = 0;

    Student = {
        name: "",
        mail: "",
        phone: "",
    }

    state = {
        TextVal: " ",
        studentsArray: [{
            name:'shay',
            mail:"s@gmail.com",
            phone:"11111",
        }],
        num: 15,
    }

    changeName(event, i) {
        let students = [...this.state.studentsArray]
        students[i].name = event.target.value;
        this.setState({ studentsArray: students })
        console.log(event.target.value, this.state.studentsArray)
    }

    changeName1 = (event,type) => {
        // console.log(event.target.value , type)
        if (type == 0)
            this.Student.name = event.target.value;
        if (type == 1)
            this.Student.mail = event.target.value;
        if (type == 2)
            this.Student.phone = event.target.value;

        console.log(this.Student)
    }

    insertStudent = () => {
        let st = {...this.Student};
        let arr = [...this.state.studentsArray];
        arr.push(st);
        console.log("Arr : " , arr);
        this.setState({studentsArray:arr})

        this.Student = {
            name: "",
            mail: "",
            phone: "",
        }
    
        console.log(this.state);
    }

    printStudents=()=>
    {
        let arr = [];
        for (let item of this.state.studentsArray)
        {
            arr.push(<tr>
                    <td>{item.name}</td>
                    <td>{item.mail}</td>
                    <td>{item.phone}</td>
                </tr>)
        }

        return arr;
    }

    render() {
        // let Input = (<div>
        //     {
        //         this.state.studentsArray.map((item, index) => {
        //             return <InputT changed={(event) => this.changeName(event,index)} name={item.name} key={item.id}></InputT>
        //         })}
        // </div>)

        return (
            <div className="row">
                <div className="col-3">
                    <Form funEventChange={this.changeName1}  buttonClick={this.insertStudent}></Form>
                </div>
                <div className="col-9">
                    <Table students={this.printStudents}></Table>
                </div>
            </div>
        );
    }
}
export default App;
