import React, { Component } from 'react';
import './App.css';
//import Input from './Components/Input';
import Table from './Components/Table';
import TableRev from './Components/TableRev';
import axios from "axios";
// import { BrowserRouter } from 'react-router-dom'
// import { Route, Link } from 'react-router-dom';
import Modal from 'react-modal';
import { Button, Form} from 'react-bootstrap';

const customStyles = {
  content : {
    top                   : '50%',
    left                  : '50%',
    right                 : 'auto',
    bottom                : 'auto',
    marginRight           : '-50%',
    transform             : 'translate(-50%, -50%)'
  }
};


class App extends Component {
  state = {
    userName:"",
    comment:"",
    gameId:"",
    gameIdReviews:"",
    foundGames: [],
    // foundGamesByCat: [],
    modalIsOpen: false,
    foundReviews: [],
  };

componentDidMount=()=>{
  this.getAllGames();
}

getAllGames=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getAllGames`)
  .then(function(response){

    console.log(response.data);

   self.setState({ foundGames: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

printRes=()=>{

  let printArray = [];
      for (let item of this.state.foundGames)
      {
        printArray.push(<tr onClick={()=>this.getReviews(item.Id)}>
                  <td>{item.Id}</td>
                  <td>{item.teamA}</td>
                  <td>{item.teamB}</td>
                  <td>{item.ScoreA}</td>
                  <td>{item.ScoreB}</td>
                  <td>{item.Time}</td>
                  <td>{item.Category}</td>
                  <button type="button" onClick={()=>this.openModal(item.Id)} className="btn btn-primary mt-3">Add Reviews</button>
              </tr>)
      }
  return printArray; 
}

printResRev=()=>{

  let printArrayRev = [];
      for (let item of this.state.foundReviews)
      {
        printArrayRev.push(<tr>
                  <td>{item.userName}</td>
                  <td>{item.Comment}</td>
                  </tr>)
      }
  return printArrayRev; 
}


handleClick = (category) => {
  // event.preventDefault();
   // console.log(category);
    var self=this;   
    axios.get(`http://localhost:5000/foundGamesByCat?category=${category}`)
    .then(function(response){

      console.log(response.data);
      self.setState({ foundGames: response.data });
    })
    .catch(function(error){
       console.log(error);
    });
  }

  openModal = (id) =>{

    this.setState({gameId: id});

    console.log(id);

   this.setState({modalIsOpen: true});

  }

  sendReviews = (event) => {
      event.preventDefault();
      let userName=this.state.userName;
      let comment=this.state.comment;
      console.log(this.state.id);

      axios.get(`http://localhost:5000/addReviews?userName=${userName}&comment=${comment}&gameId=${this.state.gameId}`)
      .then(function(response){

        console.log(response.data);

      })
      .catch(function(error){
         console.log(error);
      });

      this.setState({modalIsOpen: false});
    }

  getReviews=(gameIdReviews)=>{
      var self=this;
      console.log(gameIdReviews);
      axios.get(`http://localhost:5000/getReviews?gameIdReviews=${gameIdReviews}`)
      .then(function(response){

        console.log(response.data);

       self.setState({ foundReviews: response.data });
      })
      .catch(function(error){
         console.log(error);
      });
    }

  render() {
    return (

      <div className="container">
      <div className="row">
      <div className="col-3 offset-1">
      <button type="button" onClick={()=>this.handleClick('Football')} className="btn btn-primary mt-3">Football</button>
      </div>
      <div className="col-3 offset-1">
      <button type="button" onClick={()=>this.handleClick('BasketBall')} className="btn btn-primary mt-3">BasketBall</button>
      </div>
      </div>
      <div className="row">
      <Table games={this.printRes}></Table>
      </div>
      <div className="row">
      <TableRev reviews={this.printResRev}></TableRev>
      </div>
      {/* <Route path="/" exact component={Table}/> */}
     {/* <Route path="/addReviews" exact component={Input} /> */}



     <Modal
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Add Reviews"
        >

 <Form>
  <Form.Group controlId="formBasicName">
    <Form.Label>Name</Form.Label>
    <Form.Control type="userName" placeholder="Enter your name" onChange = {(event,target) => this.setState({userName:event.target.value})}/>
    <Form.Text className="text-muted">
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicMail">
    <Form.Label>Comment</Form.Label>
    <Form.Control type="comment" placeholder="Comment" onChange = {(event,target) => this.setState({comment:event.target.value})}/>
  </Form.Group>

<Button variant="primary" type="submit" onClick={(event) => this.sendReviews(event)}>
    Send
  </Button>
</Form>
        </Modal>

      </div>



    );
  }
}

export default App;
