import React, { Component } from 'react';
import './App.css';
import Input from './Components/Input';
import Table from './Components/Table';
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'
import { Route, Link } from 'react-router-dom';

class App extends Component {
  state = {
    name:"",
    mail:"",
    id:"",
    foundGames: [],
    foundGamesByCat: []

  };

componentDidMount=()=>{
  this.getAllGames();
}

getAllGames=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getAllGames`)
  .then(function(response){

    console.log(response.data);

   self.setState({ foundGames: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

printRes=()=>{

  let printArray = [];
      for (let item of this.state.foundGames)
      {
        printArray.push(<tr>
                  <td>{item.Id}</td>
                  <td>{item.teamA}</td>
                  <td>{item.teamB}</td>
                  <td>{item.ScoreA}</td>
                  <td>{item.Slices}</td>
                  <td>{item.ScoreB}</td>
                  <td>{item.Time}</td>
                  <td>{item.Category}</td>
                  <td><Link to="/addReviews" className={'btn btn-lg btn-primary'}>Input</Link> */}</td>
              </tr>)
      }
  return printArray; 
}


handleClick = (category) => {
  // event.preventDefault();
   // console.log(category);
    var self=this;   
    axios.get(`http://localhost:5000/foundGamesByCat?category=${category}`)
    .then(function(response){

      console.log(response.data);
      self.setState({ foundGames: response.data });
    })
    .catch(function(error){
       console.log(error);
    });
  }


  render() {
    return (
      <BrowserRouter>
      <div className="container">
      <div className="row">
      <div className="col-3 offset-3">
      <button type="button" onClick={()=>this.handleClick('Football')} className="btn btn-primary mt-3">Football</button>
      </div>
      <div className="col-3 offset-3">
      <button type="button" onClick={()=>this.handleClick('BasketBall')} className="btn btn-primary mt-3">BasketBall</button>
      </div>
      </div>
      <Table data={this.state.foundGames}></Table>
      {/* <Route path="/" exact component={Table}/> */}
      <Route path="/addReviews" exact component={Input} />
      </div>

      </BrowserRouter>

    );
  }
}

export default App;
