import React, { Component } from 'react';
import './App.css';
import Card from './Components/Card';
import StartPage from './Components/StartPage';
import HeaderComp from './Components/header';
//import Buttons from './Components/Buttons'
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'

class App extends Component {
  state = {
    diameter:"",
    slices:"",
    mail:"",
    id:"",
    allCategory: [],
    allSubCategory: [],
    allProducts: [],
    products:"",
    allVacation:[]

  };

  getAllVacations=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getAllVacation`)
  .then(function(response){

   console.log(response.data);

   self.setState({ allVacation: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
} 

printCards=()=>{
  let printArray  = [];
  for(let item of this.state.allVacation)
  {
      console.log(item);
      printArray.push(<Card item={item}/>)

  }

  return printArray;
}

render() {

    return (
      <BrowserRouter>
      <div className="App">
      <div className="container">
        <div className="row"> 
        <HeaderComp></HeaderComp>         

        </div>

        <div className="row"> 

          <StartPage></StartPage>

        </div> 

      </div>
      </div>
      </BrowserRouter>
    );
  }
}

export default App;
