import React, { Component } from 'react';
import './App.css';
import Table from './Components/Table';
import HeaderComp from './Components/header';
import Buttons from './Components/Buttons'
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'

class App extends Component {
  state = {
    diameter:"",
    slices:"",
    mail:"",
    id:"",
    allCategory: [],
    allSubCategory: [],

  };

componentDidMount=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getCategories`)
  .then(function(response){

   console.log(response.data);

   self.setState({ allCategory: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
} 

getSubCategory=(id)=>{
  console.log("id:", id)
  var self=this;
  axios.get(`http://localhost:5000/getSubCategories?id=${id}`)
  .then(function(response){

   console.log(response.data);

   self.setState({ allSubCategory: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
} 

  render() {
    return (
      <div className="container">
       <HeaderComp tx="Pussy Shop"></HeaderComp>
       <hr />

       <div className="row">
        <HeaderComp tx="Please, choose category:"></HeaderComp>
        </div>
        <div className="row">
        {this.state.allCategory.map( (item) => {
                return (
                    <div className="col-3 offset-2">
                        <Buttons tx={item.Name} id={item.Id} getSubCat={() => this.getSubCategory()}></Buttons>
                    </div>
                )
            })}
          </div>
          <br />
          <hr />
          <br />

        <div className="row">
          {/* <h3>Current Pizza Orders:</h3> 
          </div>  
          <div className="row">   
          <Table data={this.state.allPizza}></Table> */}

        </div> 

      </div>
    );
  }
}

export default App;
