import React from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Route, Link, Switch,Redirect} from 'react-router-dom';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

import './AddVacation.css';

class AddModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: true
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    if (this.state.modal == false){ 
        return  <Redirect to="/HomeAdmin"/>
       }
    return (

        <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
        <ModalHeader toggle={this.toggle}>Enter your comment for this game</ModalHeader>
        <ModalBody>
        <FormGroup>
        <Label for="'exampleEmail">Name</Label>
        <Input type="text" name="Name" id="exampleName" placeholder="Enter your name" onChange={this.handleChange} />
        <Label for="exampleText">Text Area</Label>
        <Input type="textarea" name="text" id="exampleText" onChange={this.handleChange} />
      </FormGroup>
        </ModalBody>
        <ModalFooter>            
          <Button color="primary"  onClick={() =>this.AddComment(this.props.id)}>Add comment</Button>
          <Button color="secondary" onClick={this.toggle}>Cancel</Button>
        </ModalFooter>
      </Modal>

    );
  }
}

export default AddModal;
