            printArray.push(<Card key={item.id} data={item}/>)

		}
		console.log('printArray',printArray);
        return printArray;
      }


  render() {
    return (

			  <div className="container">
				  <div className="row">
					  {this.printCards()}
				  </div>
			  </div>

    );
  }
}

export default Home;
