import React, { Component } from 'react';
import './App.css';
//import Left from './Components/LeftPage'
//import InputT from './Components/InputText'
import Buttons from './Components/Buttons'
import Box from './Components/Box'

class App extends Component {
    state={
      colors:['red','blue','red']
    }

    getColor=(color)=>
    {
        console.log(color)
    }
    

    render() {
        return (
           <div className="App">
            <div className="row">
            {this.state.colors.map( (color, index) => {
                return (
                    <div key={index} className="col-3">
                        <Box color={color} getColorFun={this.getColor}></Box>
                    </div>
                )
            })}
            </div>
                 {/* <div className="col-3">
                     <Box color={} getColorFun={this.getColor}></Box>
                // </div>    
                // <div className="col-3">    
                //     <Box color="blue" getColorFun={this.getColor}></Box>
                // </div>    
                // <div className="col-3">    
                //     <Box color="green" getColorFun={this.getColor}></Box>
                // </div> */}
          </div>
        )
      }
    }

export default App;
