import React, { Component } from 'react';
import './App.css';
import Login from './Login.js'; 

import axios from "axios";

class App extends Component {
  state = {
    name:"",
    password:"",

    id:"",
    foundStudents: [],
    selectStudent:[],
    endpoint: "localhost:4002",
    color: 'orange',
    studentForInsert: {},

    checkCookie:"",
  };


  // sending sockets
  ButtonInsert = () => {

  let name=this.state.name;
  let password=this.state.age;

    var self=this;
  axios.get(`http://localhost:4002/login?name=${name}&password=${password}`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ foundStudents: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
  }

componentDidMount=()=>{
  var self=this;
  axios.get(`http://localhost:4002/checkCookie`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ checkCookie: response.data });
  })
  .catch(function(error){
     console.log(error);
  });



}

  handleChange=(event,type)=> {
    if (type === 0){
      this.setState({name: event.target.value});
      console.log("change",this.state.name)
    }
    if (type === 1) {
      this.setState({age: event.target.value});
      console.log("change",this.state.password)
    }

  }


  render() {
    return (
      <div className="container">

      <Login></Login>  
      </div>
    );
  }
}

export default App;
