import React, { Component } from 'react';
import './App.css';
import Table from './Components/Table';
import axios from "axios";

//import { BrowserRouter } from 'react-router-dom'
//import Blog from './Blog/Blog';
//import Buttons from './Buttons/buttons';

class App extends Component {
  state = {
    name:"",
    age:"",
    mail:"",
    foundStudents: []
  };

  handleChange=(event,type)=> {
    if (type === 0){
      this.setState({name: event.target.value});
      //console.log(this.student.name)
    }
    if (type === 1) {
      this.setState({age: event.target.value});
      //console.log(this.student.mail)
    }
    if (type === 2) {
      this.setState({mail: event.target.value});
      //console.log(this.student.mail)
    }
  }

  ButtonSearch=()=>
  { 

    let name=this.state.name;
    let age=this.state.age;
    let mail=this.state.mail;
   // console.log(searchPrice);
    axios.get(`http://localhost:5000/insertStudents?name=${name}&age=${age}&mail=${mail}`)
    .then(function(response){

      console.log(response.data);
      //console.log(self.state.arrPosts);
     // self.setState({ foundStudents: response.data });
    })
    .catch(function(error){
       console.log(error);
    });
  }

  printRes=()=>{
    var self=this;
    axios.get(`http://localhost:5000/getStudents`)
    .then(function(response){

      console.log(response.data);
      //console.log(self.state.arrPosts);
     self.setState({ foundStudents: response.data });
    })
    .catch(function(error){
       console.log(error);
    });

   // let printProducts  = [...this.state.foundProducts];
    let printArray = [];
        for (let item of this.state.foundStudents)
        {
          printArray.push(<tr>
                    <td>{item.name}</td>
                    <td>{item.age}</td>
                    <td>{item.mail}</td>
                </tr>)
        }
    return printArray; 
  }

  render() {
    return (
      <div className="container">
        <div className="row">


          <label htmlFor="name">
            Please, enter the name.
          </label>
          <input
            type="text"
            id="name"
            onChange={(event) => this.handleChange(event,0)}
            value={this.state.name}
          />
         </div> 
         <div className="row">
          <label htmlFor="age">
            Please, enter the age.
          </label>
          <input
            type="number"
            id="age"
            onChange={(event) => this.handleChange(event,1)}
            value={this.state.age}
          />
          </div>
          <div className="row">
          <label htmlFor="mail">
            Please, enter the mail.
          </label>
          <input
            type="text"
            id="mail"
            onChange={(event) => this.handleChange(event,2)}
            value={this.state.mail}
          />
         </div>
          <button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonSearch()}>Insert</button>


        <div className="row">
        <h3>All students: </h3>
        </div>
        <div className="row">

                   <Table students={this.printRes}></Table>

        </div> 


      </div>
    );
  }
}

export default App;
