                    <div className="col-10">
                      <h3>Delete vacation</h3>
                    </div>  
                    <div className="col-2"> 
                      <i className="fas fa-times" onClick={() => this.closeModal()}></i>
                    </div>
                    </div>
                      <hr></hr>
                      <p>Are you sure you want to delete vacation </p>
