import React, { Component } from 'react';
import MyCard from './Components/MyCard';
import axios from "axios";
import { Route, Link } from 'react-router-dom';


class Home extends Component {
    state = {
      name:"",
      mail:"",
      id:"",
      foundWorkes: [],

    };

    componentDidMount=()=>{
        var self=this;
        axios.get(`http://localhost:5000/getAllVacation`)
        .then(function(response){

         console.log(response.data);

         self.setState({ allVacation: response.data });
        })
        .catch(function(error){
           console.log(error);
        });
      } 

      printCards=()=>{
        let printArray  = [];
        for(let item of this.state.foundWorkes)
        {
            console.log(item);
            printArray.push(<MyCard checkOn={item.role} name={item.name} mail={item.mail} id={item.id}/>)

        }
        return printArray;
      }


  render() {
    return (

      <div className="container">
      {/* <div className="row"> */}
      {/* <Button variant="primary" type="submit" onClick={(event) => this.handleClick(event)}> */}
      {/* <Link to="/addworker" className={'btn btn-lg btn-primary'}>Input</Link> */}
      {/* </Button> */}
      {/* </div> */}

      <div className="row">
      {/* <Button variant="primary" type="submit" onClick={(event) => this.handleClick(event)}> */}
      {/* <Link to="/"></Link> */}
      <Link to="/addworker" className={'btn btn-lg btn-primary'}>Input</Link>

      {/* </Button> */}
      </div>

      <div className="row">
      <h3>All workes: </h3>
      </div>

      <div className="row">
           {this.printCards()}
      </div>
      {/* <Route path="/addworker" exact component={Input} /> */}
      </div>

      // {/* <div className="col-4">
      // <h1>Add workes:</h1>
      //   <Input></Input> 
      // </div> */}

    );
  }
}

export default Home;
