    render() {
        return (
          <BrowserRouter>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <MDBCard>
                    <MDBCardBody>
                      <form>
                        <p className="h4 text-center py-4">Login</p>
                        <div className="grey-text">
                          <MDBInput
                            label="Your name"
                            icon="user"
                            group
                            type="text"
                            validate
                            error="wrong"
                            success="right"
                          />
                          <MDBInput
                            label="Your password"
                            icon="lock"
                            group
                            type="password"
                            validate
                          />
                        </div>
                        <div className="text-center py-4 mt-3">
                          <MDBBtn color="cyan" type="submit">
                            LOGIN
                          </MDBBtn>
                        </div>
                      </form>
                      <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? <Link to="/registration">Sign Up</Link></p>
                  {/* <p>Forgot Password?</p> */}
                </div>
              </MDBModalFooter>
                    </MDBCardBody>
                  </MDBCard>
                </MDBCol>
              </MDBRow>
            </MDBContainer>

            </BrowserRouter>
          );
        };
      }   
  export default Login;
