import React, { Component } from 'react';
import './App.css';
import Input from './Components/Input';
import Table from './Components/Table';
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'
import { Route, Link } from 'react-router-dom';

class App extends Component {
  state = {
    name:"",
    mail:"",
    id:"",
    foundGames: [],
    foundGamesByCat: []

  };

componentDidMount=()=>{
  this.getAllGames();
}

getAllGames=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getAllGames`)
  .then(function(response){

    console.log(response.data);

   self.setState({ foundGames: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

handleClick = (type) => {
  event.preventDefault();
  if (type=1)
  {
    let category='Football';
  } 
  else  {
    let category='BasketBall'
  };

    console.log(category);

    axios.get(`http://localhost:5000/foundGamesByCat?category=${category}`)
    .then(function(response){

      console.log(response.data);

    })
    .catch(function(error){
       console.log(error);
    });
  }


  render() {
    return (
      <BrowserRouter>
      <div className="container">
      <div className="row">
      <div className="col-4">
      <button type="button" onClick={()=>this.handleClick(1)} className="btn btn-primary mt-3">Football</button>
      </div>
      <div className="col-4">
      <button type="button" onClick={()=>this.handleClick(2)} className="btn btn-primary mt-3">BasketBall</button>
      </div>
      </div>

      <Route path="/" exact component={Table}/>
      <Route path="/addReviews" exact component={Input} />
      </div>

      </BrowserRouter>

    );
  }
}

export default App;
