import React from 'react';
import { Route, Link } from 'react-router-dom';

import './Post.css';


const post = (props) => {
    console.log("Props : " ,props,props.index)
    return <div className="Post">
        {/* <article>
            <Link  to={{ pathname: "fullPost/" + props.id }}>
            <h1>{props.title}</h1></Link>
        </article> */}
              <Link  to={{ pathname: "fullPost/" + props.index }}></Link>  

              <div className='card' style='width: 18rem;'>
              <img className='card-img-top' src='http://www.mystor.co.il/php/'{...props.image} alt='Card image cap'></img>
              <div className='card-body'>
              <h5 className='card-title'>{props.name}</h5>
              <p className='card-text'>{props.desc}</p>
              <p className='card-text'>Price:  {props.new_price}</p>
              </div>
              </div>

    </div>
}
export default post;
