import React, { Component } from 'react';
import axios from "axios";

import MuiThemeProvider from 'material-ui/styles/MuiThemeProvider';
import AppBar from 'material-ui/AppBar';
import RaisedButton from 'material-ui/RaisedButton';
import TextField from 'material-ui/TextField';

class Login extends Component {
  state = {
    username:"",
    password:"",

    id:"",
    foundStudents: [],
    selectStudent:[],
    endpoint: "localhost:4002",
    color: 'orange',
    studentForInsert: {},

    checkCookie:"",
    isLogin:"",
  };

  ButtonLogin= () => {

    let name=this.state.name;
    let password=this.state.password;

    var self=this;
    axios.get(`http://localhost:4000/checkLogin?name=${name}&password=${password}`,{withCredentials:true})
    .then(function(response){

      console.log(response.data);

     self.setState({ isLogin: response.data });
    })
    .catch(function(error){
       console.log(error);
    });
    }

    handleChange=(event,type)=> {
      if (type === 0){
        this.setState({name: event.target.value});
        console.log("change",this.state.name)
      }
      if (type === 1) {
        this.setState({password: event.target.value});
        console.log("change",this.state.password)
      }

    }

    render() {
        return (

<div>
        <MuiThemeProvider>
          <div>
          <AppBar
             title="Login"
           />
           <TextField
             hintText="Enter your Username"
             floatingLabelText="Username"
             onChange = {(event,newValue) => this.setState({username:newValue})}
             />
           <br/>
             <TextField
               type="password"
               hintText="Enter your Password"
               floatingLabelText="Password"
               onChange = {(event,newValue) => this.setState({password:newValue})}
               />
             <br/>
             <RaisedButton label="Submit" primary={true} style={style} onClick={(event) => this.handleClick(event)}/>
         </div>
         </MuiThemeProvider>
      </div>
            );
        }
      }

  export default Login;
