import Box from './Components/Box'

class App extends Component {
    state={
      colors:['red','blue','green'],
      colorsBox:[]
    }

    getColor=(color)=>
    {
        let getColor=[...this.state.colorsBox];
        let newColor=color;
        getColor.push(newColor);
        console.log(color);
        console.log(getColor);
        this.setState({colorsBox:getColor});
    }
    
