    render() {
        return (
           <div className="App">
            <div className="row">
            {this.state.colors.map( (color, index) => {
                return (
                    
                    <div key={index} className="col-3">
                        {/* <Box color={color} getColorFun={this.getColor}></Box> */}
                        <Buttons color={color} getColorFun={this.getColor}></Buttons>
                    </div>
                )
            })}
            </div>
            <div className="row">
            {this.state.colorsBox.map( (color, index) => {
                return (
                    
                    <div key={index} className="col-3">
                        <Box color={color}></Box>
                        {/* <Buttons color={color} getColorFun={this.getColor}></Buttons> */}
                    </div>
