import React, { Component } from 'react';
import { Route, Link } from 'react-router-dom';

//import Registration from './Registration';

import './StartPage.css';

class Registration extends Component {
    state = {
        userName:"",
        firstName:"",
        lastName:"",
        password:"",

    }

    handleChange=(event,type)=> {
        if (type === 0){
          this.setState({name: event.target.value});
          console.log("change",this.state.firstName)
        }
        if (type === 1) {
          this.setState({age: event.target.value});
          console.log("change",this.state.lastName)
        }
        if (type === 2) {
            this.setState({age: event.target.value});
            console.log("change",this.state.userName)
          }

          if (type === 3) {
            this.setState({age: event.target.value});
            console.log("change",this.state.password)
          }


      }
    render () {
        return (
            <div className="container">
            <h1>Registration</h1>
            <div className="row">

                <label htmlFor="name">
                  Please, enter your first namr.
                </label>
                <input
                  type="text"
                  id="name"
                  onChange={(event) => this.handleChange(event,0)}
                  value={this.state.firstName}
                />
               </div> 
               <div className="row">

                <label htmlFor="name">
                  Please, enter your last name.
                </label>
                <input
                  type="text"
                  id="name"
                  onChange={(event) => this.handleChange(event,1)}
                  value={this.state.lastName}
                />
               </div> 

              <div className="row">

                <label htmlFor="name">
                  Please, enter your user name.
                </label>
                <input
                  type="text"
                  id="name"
                  onChange={(event) => this.handleChange(event,2)}
                  value={this.state.userName}
                />
               </div> 
               <div className="row">
                <label htmlFor="age">
                  Please, enter your password.
                </label>
                <input
                  type="number"
                  id="age"
                  onChange={(event) => this.handleChange(event,3)}
                  value={this.state.password}
                />
                </div>

                <button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonEnter()}>OK</button>
                </div>

            );
        }
    }

    export default Registration;
