import React, { Component } from 'react';
import './App.css';
//import Left from './Components/LeftPage'
//import InputT from './Components/InputText'
import Buttons from './Components/Buttons'
import Box from './Components/Box'

class App extends Component {
  number=0;
    state={
      colors:['red','blue','green'],
      colorsBox:[],
      
    }

    getColor=(color)=>
    {
        let getColor=[...this.state.colorsBox];
        let newColor=color;
        let num=this.state.number;
        num++;
        this.setState({number:num});
        getColor.push(newColor);
        console.log(color);
        console.log(getColor);
        this.setState({colorsBox:getColor});
    }

    print = () => {
      let colorsBox  = [...this.state.colorsBox];
      let printArray=[];
      colorsBox.forEach(function(color)
      {
        printArray.push(<Box color={color}  number={this.number} delBoxFun={this.delBox}></Box>); 
        this.number++;
      });
      return printArray;  
    }

    // delBox=(number)=>{
    //    let getColor1=[...this.state.colorsBox];
    //    let numBox=number;
    //    getColor1.splice(getColor1.indexOf(numBox), 1); 
    // }
  
    render() {
        return (
           <div className="App">
            <div className="row">
            {this.state.colors.map( (color, index) => {
                return (
                    
                    <div key={index} className="col-3">
                        {/* <Box color={color} getColorFun={this.getColor}></Box> */}
                        <Buttons color={color} getColorFun={this.getColor}></Buttons>
                    </div>
                )
            })}
            </div>
            <div className="row">
            {this.print()}
            </div>
          </div>
        )
      }
    }

export default App;
