import React, { Component } from 'react';
import { Button, Form} from 'react-bootstrap';

import {BootstrapTable, TableHeaderColumn} from 'react-bootstrap-table';
import '../../node_modules/react-bootstrap-table/css/react-bootstrap-table.css';
import './Table.css';
import axios from "axios";

class table extends Component {
    state = {

        name:"",
        age:"",
        mail:"",

      };

    render() {
    console.log("props : " , this.props)

handleChange=(event,type)=> {
    if (type === 0){
      this.setState({name: event.target.value});
      console.log("change",this.state.name)
    }
    if (type === 1) {
      this.setState({age: event.target.value});
      console.log("change",this.state.age)
    }
    if (type === 2) {
      this.setState({mail: event.target.value});
      console.log("change",this.state.mail)
    }
  }

    return (
        <div>
          <Form>
  <Form.Group controlId="formBasicEmail">
    <Form.Label>Email name</Form.Label>
    <Form.Control type="name" placeholder="Enter name" onChange = {(event,newValue) => this.setState({name:newValue})}/>
    <Form.Text className="text-muted">
      {/* We'll never share your email with anyone else. */}
    </Form.Text>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Age</Form.Label>
    <Form.Control type="age" placeholder="Age" onChange = {(event,newValue) => this.setState({age:newValue})}/>
  </Form.Group>

  <Form.Group controlId="formBasicPassword">
    <Form.Label>Mail</Form.Label>
    <Form.Control type="mail" placeholder="Mail" onChange = {(event,newValue) => this.setState({mail:newValue})}/>
  </Form.Group>

  {/* <Form.Group controlId="formBasicChecbox">
    <Form.Check type="checkbox" label="Check me out" />
  </Form.Group> */}
  <Button variant="primary" type="submit">
    Submit
  </Button>
</Form>
        </div>
      );
}
}

export default table
