    //   console.log("Login: ",response.data);

    //  self.setState({ isLoginSuccess: response.data });

    //  console.log("isLoginSuccess:", this.state.isLoginSuccess);
    // })
    // .catch(function(error){
    //    console.log(error);
    // });
    }



    render() {
        return (
          <BrowserRouter>
          <div>
            <MDBContainer>
              <MDBRow>
                <MDBCol md="6">
                  <MDBCard>
                    <MDBCardBody>
                      <form>
                        <p className="h4 text-center py-4">Login</p>
                        <div className="grey-text">
                          <MDBInput
                            label="Your name"
                            icon="user"
                            group
                            type="text"
                            validate
                            error="wrong"
                            success="right"
                            onChange = {(event,target) => this.setState({username:event.target.value})}
                            // onChange={this.changeName}
                            value={this.state.username}
                          />
                          <MDBInput
                            label="Your password"
                            icon="lock"
                            group
                            type="password"
                            validate
                            onChange = {(event,target) => this.setState({password:event.target.value})}
                            // onChange={this.changePassword}
                            value={this.state.password}
                          />
                        </div>
                        <div className="text-center py-4 mt-3">
                          <MDBBtn color="cyan" type="submit" onClick={this.sendLogin}>
                            LOGIN
                          </MDBBtn>
                        </div>
                      </form>
                      <MDBModalFooter>
                <div className="font-weight-light">
                  <p>Not a member? <Link to="/registration">Sign Up</Link></p>
                  {/* <p>Forgot Password?</p> */}
                </div>

              </MDBModalFooter>
                    </MDBCardBody>
                  </MDBCard>
                </MDBCol>
              </MDBRow>

            </MDBContainer>
            <Route path="/registration" exact component={Registration} />
            </div>
            </BrowserRouter>
          );
        };
      }   
  export default Login;
