import React, { Component }  from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

import { FontAwesomeIcon} from '@fortawesome/react-fontawesome'
import { faEdit} from '@fortawesome/free-solid-svg-icons'
import { MDBIcon } from "mdbreact";
//import $ from "jquery";

import './ButtonDel.css';
// import ToggleButton from 'react-bootstrap/ToggleButton'
// import {Card,Button} from 'react-bootstrap';
import axios from "axios";

class ButtonDel extends Component {

    constructor(props) {
        super(props);
        this.state = {
          modal: false
        };

        this.toggle = this.toggle.bind(this);
      }

      toggle() {
        this.setState(prevState => ({
          modal: !prevState.modal
        }));
      }


handleClick = (idVac) => {
   // event.preventDefault();

    console.log("props : " , this.props)
    console.log('idVac',idVac) 
    this.toggle();

    // axios.get(`http://localhost:5000/deleteVacations?idVac=${idVac}`)
    // .then(function(response){

    //   console.log(response.data);

    // })
    // .catch(function(error){
    //    console.log(error);
    // });

}


render() {

    console.log("props : " , this.props)

    return (
     <div>
       {/* <i class="far fa-trash-alt" onClick={() =>this.handleClick(this.props.idVac)}></i> */}

       <i class="far fa-trash-alt" onClick={() =>this.toggle()}></i>

       <Modal isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Delete vacation</ModalHeader>
          <ModalBody>
          <p>Are you sure you want to delete vacation </p>
          <p> to {this.props.location} on dates {this.props.dateFrom}-{this.props.dateTo}?</p>
          </ModalBody>
          <ModalFooter>
            {/* <Button color="primary" onClick={this.toggle}>Delete</Button> */}
            <Button color="primary" onClick={() =>this.handleClick(this.props.idVac)}>Delete</Button>{' '}
            <Button color="secondary" onClick={this.toggle} href="/HomeAdmin">Cancel</Button>
          </ModalFooter>
        </Modal>

     </div>
            ); 
}
}

export default ButtonDel
