import React, { Component } from 'react';
import axios from "axios";
import ReactModalLogin from "react-modal-login";
import Modal from 'react-bootstrap/Modal'

import Button from 'react-bootstrap/Button'
import { Route, Link } from 'react-router-dom';

require("react-bootstrap/ModalHeader")
require("react-bootstrap/ModalDialog")
require("react-bootstrap/ModalFooter")
require("react-bootstrap/ModalTitle")
require("react-bootstrap/ModalBody")

//import Login from './Login';
//import Registration from './Registration';

//import './Login.css';

class Login extends Component {
    state = {
        name:"",
        password:"",
        checkLogOn:"",
        show: false,

    }

      handleClose() {
        this.setState({ show: false });
      }

    handleChange=(event,type)=> {
        if (type === 0){
          this.setState({name: event.target.value});
          console.log("change",this.state.name)
        }
        if (type === 1) {
          this.setState({age: event.target.value});
          console.log("change",this.state.password)
        }

      }

      logOn=()=>
      { 
        let name=this.state.name;

        let password=this.state.password;
       // console.log(searchPrice);
        axios.get(`http://localhost:5000/logOn?name=${name}&password=${password}`)
        .then(function(response){

          console.log(response.data);
          //console.log(self.state.arrPosts);
         //self.setState({ foundStudents: response.data });
        })
        .catch(function(error){
           console.log(error);
        });

      }

    render () {
        return (



<div>
        {/* <button onClick={() => this.openModal()}>Open Modal</button> */}

<Modal.Dialog>
  <Modal.Header closeButton>
    <Modal.Title>Modal title</Modal.Title>
  </Modal.Header>

  <Modal.Body>
                <div className="row">

                   <label htmlFor="name">
                     Please, enter your user name.
                   </label>
                   <input
                    type="text"
                    id="name"
                    onChange={(event) => this.handleChange(event,0)}
                    value={this.state.name}
                  />
                 </div> 
                 <div className="row">
                  <label htmlFor="age">
                    Please, enter your password.
                  </label>
                  <input
                    type="number"
                    id="age"
                    onChange={(event) => this.handleChange(event,1)}
                    value={this.state.password}
                  />
                  </div>
  </Modal.Body>

  <Modal.Footer>
    <Button variant="secondary" onClick={this.handleClose} >Close</Button>
    <Button variant="primary" onClick={this.logOn}>Log on</Button>
    <br></br>
    <Link to="/registration">Registration</Link>
  </Modal.Footer>

</Modal.Dialog>;


      </div>



