import React, { Component } from 'react';
import axios from 'axios';

import './FullPost.css';

class FullInfo extends Component {
    state = {
        loadedPost: null
    }

    componentDidMount () {
        console.log("FullPost" , this.props , this.props.match.params.id)
        axios.get('http://404040.co.il/shop/product.php?id=' + this.props.match.params.id)
                    .then( response => {
                        console.log(response);
                        this.setState( { loadedPost: response.data } );
        } );
    }

    render () {
        let post = "";

        if ( this.state.loadedPost ) {
            post = (
                <div className="FullInfo">
                    {/* <img className="pic" src={"http://www.mystor.co.il/php/"+this.state.loadedPost.image}></img> */}
                    <h1>{this.state.loadedPost.name}</h1>
                    {/* <p>{this.state.loadedPost.body}</p> */}
                </div>
            );
        }
        return post;
    }
}

export default FullInfo;
