import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';

class App extends Component {
  state={
    numberMy:0,
    numberArray:[],
  }

ShowNumber=()=>
{ 
  this.setState({num1:10});
  this.setState({name:"Nelly"});
  console.log(this.state);
  for (let item of  this.state.nameArray)
  {
    console.log(item)
  }
}

printArray=()=>
{
  let numbersNew=[];

  for (let n of this.state.numberArray)
  {
    numbersNew.push({n},)
  }

  return numbersNew;
}

addToArray=()=>
{
  let numNew=this.state.numberMy;
  let numbers=this.state.numberArray;
  numbers.push(numNew);
  numNew=numNew+1;
  // this.state.numberArray.push(state.number);
  this.setState({numberMy:numNew});
  this.setState({numberArray:numbers});
  console.log(this.state.numberArray);
  return;

}


  render() {
    return (
      <div className="App">
        {/* <div>My name is: {this.state.name}.</div>
        <div>My friend`s names are: {this.state.nameArray[0]} and {this.state.nameArray[1]}.</div>
        <div>We are : {this.state.num} years old.</div> */}
        {/* <div>{this.state.num1}</div>
        <div>{this.state.name}</div> */}
        <div>{this.printArray()}</div>
        {/* <div>{this.state.numberArray}</div> */}
        {/* <button onClick={this.ShowNumber}>Change</button> */}
        <button onClick={this.addToArray}>Add</button>
      </div>
    );
  }
}

export default App;
