import React, { Component } from 'react';
import './App.css';
import Table from './Components/Table';
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'

class App extends Component {
  state = {
    diameter:"",
    slices:"",
    mail:"",
    id:"",
    foundStudents: [],
    selectStudent:[]
  };

ButtonBuild=()=>{
 for (let i=0; i<100; i++) {

    let diameter= Math.floor(Math.random()* (50 - 10) + 10);

    let slices=Math.floor(Math.random()* (8 - 3) + 3);

    let toppings=Math.floor(Math.random()*4);

    let price=5*toppings+diameter;

    axios.get(`http://localhost:5000/insertPizza?diameter=${diameter}&slices=${slices}&toppings=${toppings}&price=${price}`)
    .then(function(response){

      console.log(response.data);

    })
    .catch(function(error){
       console.log(error);
    });

  }
}


  printRes=()=>{

    let printArray = [];
        for (let item of this.state.foundStudents)
        {
          printArray.push(<tr>
                    <td>{item.name}</td>
                    <td>{item.age}</td>
                    <td>{item.mail}</td>
                    <td><button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonUpdate(item.id)}>Update</button>
       </td>
                    <td><button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonDelete(item.id)}>Delete</button>
       </td>
                </tr>)
        }
    return printArray; 
  }

  render() {
    return (
      <div className="container">

          <button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.printRes()}>Build</button>

          <button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonPrint()}>Print</button>
          <br /><br />
        <div className="row">
        <h3>All pizza: </h3>
        </div>
        <br />
        <div className="row">

                   <Table pizza={this.printRes}></Table>

        </div> 

      </div>
    );
  }
}

export default App;
