checkFavourite = () => {
    console.log('this.props.user_id',this.props.user_id)
    if (this.state.user_id!== null) {
        return <MDBTooltip
        placement="top"
        tag="a"
        component="i"
        componentClass="fas fa-heart grey-text ml-3"
        tooltipContent="Add to watchlist"/>

    } else {
        return <MDBTooltip
        placement="top"
        tag="a"
        component="i"
        componentClass="fa fa-heart grey-text ml-3"
        tooltipContent="Add to watchlist"/>
    }
}


