import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import PrintStudents from './Components/PrintStudents'
import Input from './Components/InputText'


class App extends Component {
  state={
    studentsArray :[],
    student:{ 
      name:"",
      mail:"",
      phone:""
    }
  }

changeStudent = (event,type) => {
    if (type === 0){
      this.student.name=event.target.value;
    }
    if (type === 1) {
      this.student.mail = event.target.value;
    }
    if (type === 2) {
      this.student.phone = event.target.value;
    }
  }

  Save=() => {
    let student = this.student;
    let arr = [...this.state.studentsArray];
    arr.push(student);
    this.setState({studentsArray:arr});
  }

  print = () => {
    let students  = [...this.state.studentsArray];
    let printArray=[];
    students.forEach(function(student)
    {
      printArray.push(<PrintStudents student={student}></PrintStudents>); 
    });
    return printArray;  
  }

  render() {    

    // let Input = (<div>
    //         {
    //             this.state.studentsArray.map((item, index) => {
    //                 return <InputT changed={(event) => this.changeName(event,index)} name={item.name} key={item.id}></InputT>
    //             })}
    //     </div>)

    return (
      <div className="App">
      <div className="row">
        <div className="col-4 offset-1">
        <form>
            <div className="form-group">
                <label for="f1">Enter Name</label>
                <input type="text" className="form-control" id="f1" onChange="changeStudent(event,0)" placeholder="Enter Name" ></input>
            </div>
            <div className="form-group">
                <label for="f2">Enter Mail</label>
                <input type="text" className="form-control" id="f2" onChange="changeStudent(event,1)" placeholder="Enter Mail"></input>
            </div>
            <div className="form-group">
                <label for="f3">Enter Phone</label>
                <input type="text" className="form-control" id="f3" onChange="changeStudent(event,2)" placeholder="Enter Phone"></input>
            </div>

            <input type="buttom" className="btn btn-primary but" id="bt" value="Save" onclick="Save()"/>   
        </form>
        <div className="col-4 offset-1">
        <table className='table'>
              <thead>
              <tr>
              <th scope='col'>Name</th>
              <th scope='col'>Mail</th>
              <th scope='col'>Phone</th>
              </tr>
              </thead>
              <tbody>{this.print()}</tbody>
        </table>
        </div>
      </div>
      </div>
      </div>
    );
  }
}

export default App;
