import React, { Component } from 'react';
import './App.css';
import Input from './Components/Input';
import Table from './Components/Table';
import axios from "axios";
import { BrowserRouter } from 'react-router-dom'
import { Route, Link } from 'react-router-dom';
import Modal from 'react-modal';

class App extends Component {
  state = {
    name:"",
    mail:"",
    id:"",
    foundGames: [],
    foundGamesByCat: [],
    modalIsOpen: false,

  };

componentDidMount=()=>{
  this.getAllGames();
}

getAllGames=()=>{
  var self=this;
  axios.get(`http://localhost:5000/getAllGames`)
  .then(function(response){

    console.log(response.data);

   self.setState({ foundGames: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

printRes=()=>{

  let printArray = [];
      for (let item of this.state.foundGames)
      {
        printArray.push(<tr>
                  <td>{item.Id}</td>
                  <td>{item.teamA}</td>
                  <td>{item.teamB}</td>
                  <td>{item.ScoreA}</td>
                  <td>{item.ScoreB}</td>
                  <td>{item.Time}</td>
                  <td>{item.Category}</td>
                  {/* <td><Link to="/addReviews" className={'btn btn-lg btn-primary'} id={item.Id}>Add Reviews</Link></td> */}
                  <button type="button" id={item.Id} onClick={()=>this.openModal(id)} className="btn btn-primary mt-3">Add Reviews</button>
              </tr>)
      }
  return printArray; 
}


handleClick = (category) => {
  // event.preventDefault();
   // console.log(category);
    var self=this;   
    axios.get(`http://localhost:5000/foundGamesByCat?category=${category}`)
    .then(function(response){

      console.log(response.data);
      self.setState({ foundGames: response.data });
    })
    .catch(function(error){
       console.log(error);
    });
  }

  openModal(id) {
    this.setState({modalIsOpen: true});
  }

  closeModal() {
    this.setState({modalIsOpen: false});
  }


  render() {
    return (

      <div className="container">
      <div className="row">
      <div className="col-3 offset-3">
      <button type="button" onClick={()=>this.handleClick('Football')} className="btn btn-primary mt-3">Football</button>
      </div>
      <div className="col-3 offset-3">
      <button type="button" onClick={()=>this.handleClick('BasketBall')} className="btn btn-primary mt-3">BasketBall</button>
      </div>
      </div>
      <Table games={this.printRes}></Table>
      {/* <Route path="/" exact component={Table}/> */}
     {/* <Route path="/addReviews" exact component={Input} /> */}

     <Modal
          isOpen={this.state.modalIsOpen}
          onAfterOpen={this.afterOpenModal}
          onRequestClose={this.closeModal}
          style={customStyles}
          contentLabel="Example Modal"
        >

          <h2 ref={subtitle => this.subtitle = subtitle}>Hello</h2>
          <button onClick={this.closeModal}>close</button>
          <div>I am a modal</div>
          <form>
            <input />
            <button>tab navigation</button>
            <button>stays</button>
            <button>inside</button>
            <button>the modal</button>
          </form>
        </Modal>

      </div>



    );
  }
}

export default App;
