import React from 'react';
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Route, Link, Switch,Redirect} from 'react-router-dom';
import { Button, Form, FormGroup, Label, Input, FormText } from 'reactstrap';

class AddModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modal: true
    };

    this.toggle = this.toggle.bind(this);
  }

  toggle() {
    this.setState(prevState => ({
      modal: !prevState.modal
    }));
  }

  render() {
    if (this.state.modal == false){ 
        return  <Redirect to="/HomeAdmin"/>
       }
    return (
      <div>

        <Modal isOpen={this.state.modal} toggle={this.toggle}>
          <ModalHeader toggle={this.toggle}>Add Vacation</ModalHeader>
          <ModalBody className="addModal">
            <Form>
            <FormGroup>
          <Label for="addPicture">Picture</Label>
          <Input type="text" name="picture" id="addPicture" placeholder="" />
        </FormGroup>
            <FormGroup>
          <Label for="addName">Name</Label>
          <Input type="text" name="name" id="addName" placeholder="" />
        </FormGroup>
        <FormGroup>
          <Label for="addLocation">Location</Label>
          <Input type="number" name="Location" id="addLocation" placeholder="" />
        </FormGroup>
        <FormGroup>
          <Label for="addStartDate">From</Label>
          <Input type="date" name="startDate" id="addStartDate" placeholder="date placeholder" />
        </FormGroup>
        <FormGroup>
          <Label for="addEndDate">to</Label>
          <Input type="date" name="endDate" id="addEndDate" placeholder="date placeholder" />
        </FormGroup>
            </Form>
          </ModalBody>
          <ModalFooter>
            <Button color="primary" onClick={this.toggle}>Save</Button>{' '}
            <Button color="secondary" onClick={this.toggle}>Cancel</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default AddModal;
