import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import PrintStudents from './Components/PrintStudents'


class App extends Component {
  state={
    studentsArray :[{
      name:"Anna",
      mail:"1@1.mail.com",
      phone:"1111111"
    },
    {
      name:"Liza",
      mail:"2@2.mail.com",
      phone:"2222222"
    },
    {
      name:"Dani",
      mail:"3@3.mail.com",
      phone:"33333333"
    },
    {
      name:"Mike",
      mail:"4@4.mail.com",
      phone:"44444444"
    },
    {
      name:"Mary",
      mail:"5@5.mail.com",
      phone:"55555555"
    }]

  }

  print = () => {
    let students  = [...this.state.studentsArray];
    let printArray=[];
    students.forEach(function(student)
    // for(let i=0;i<students.length;i++)
    {
      printArray.push(<PrintStudents student={student}></PrintStudents>);

    });
    return printArray;  
  }

  render() {
    return (
      <div className="App">
        <div className="row">
        <div className="col-6 offset-2">
        <table className='table'>
              <thead>
              <tr>
              <th scope='col'>Name</th>
              <th scope='col'>Mail</th>
              <th scope='col'>Phone</th>
              </tr>
              </thead>
              <tbody>{this.print()}</tbody>
              </table>
              </div>

          {/* <div className="col-6">{this.print()}</div>     */}
        </div>
      </div>
    );
  }
}

export default App;
