import React, { Component } from 'react';
import './App.css';
import Table from './Components/Table';
import axios from "axios";

import socketIOClient from "socket.io-client";

class App extends Component {
  state = {
    name:"",
    password:"",

    id:"",
    foundStudents: [],
    selectStudent:[],
    endpoint: "localhost:4002",
    color: 'orange',
    studentForInsert: {},
  };


  // sending sockets
  ButtonInsert = () => {

   let name=this.state.name;
  let password=this.state.age;

    var self=this;
  axios.get(`http://localhost:4002/login?name=${name}&password=${password}`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ foundStudents: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
  }

send=()=>{
  const socket = socketIOClient(this.state.endpoint);
  socket.emit('insert student', this.state.studentForInsert) // change 'red' to this.state.color 
}

componentDidMount=()=>{
  this.getAllStudents();

  const socket = socketIOClient(this.state.endpoint);
    //socket.emit('insert student', this.state.studentForInsert) // change 'red' to this.state.color
    //setInterval(this.send(), 1000)

      socket.on('insert student', (studentForInsert) => {

          // let AllStud=this.state.foundStudents;
          // addStud.id=AllStud[(AllStud.length)-1].id+1;
          // AllStud.push(addStud);
          //this.setState({ foundStudents: allStud });

      })

}

getAllStudents=()=>{
  var self=this;
  axios.get(`http://localhost:4002/getStudents`)
  .then(function(response){

    console.log(response.data);
    //console.log(self.state.arrPosts);
   self.setState({ foundStudents: response.data });
  })
  .catch(function(error){
     console.log(error);
  });
}

  handleChange=(event,type)=> {
    if (type === 0){
      this.setState({name: event.target.value});
      console.log("change",this.state.name)
    }
    if (type === 1) {
      this.setState({age: event.target.value});
      console.log("change",this.state.password)
    }

  }


  render() {
    return (
      <div className="container">

        <div className="row">

          <label htmlFor="name">
            Please, enter the name.
          </label>
          <input
            type="text"
            id="name"
            onChange={(event) => this.handleChange(event,0)}
            value={this.state.name}
          />
         </div> 
         <div className="row">
          <label htmlFor="age">
            Please, enter the password.
          </label>
          <input
            type="number"
            id="age"
            onChange={(event) => this.handleChange(event,1)}
            value={this.state.password}
          />
          </div>

          <button type="button" className="btn btn-primary p-3 m-3" onClick={() =>this.ButtonInsert()}>Ok</button>

      </div>
    );
  }
}

export default App;
